<html>
	<head>	
		<title> Web Torret Example</title> 
		<script type="text/javascript" src="webtorrent.min.js"></script>
		<script type="text/javascript">
			var client = new WebTorrent()

var torrentId = 'magnet:?xt=urn:btih:fdc1a2c3bae9cb74386d18eb05bf9363d789becd&dn=Kung.Fu.Panda.3.2016.HC.HDRip.XviD.AC3-EVO&tr=udp%3A%2F%2Ftracker.leechers-paradise.org%3A6969&tr=udp%3A%2F%2Fzer0day.ch%3A1337&tr=udp%3A%2F%2Fopen.demonii.com%3A1337&tr=udp%3A%2F%2Ftracker.coppersurfer.tk%3A6969&tr=udp%3A%2F%2Fexodus.desync.com%3A6969.torrent'

client.add(torrentId, function (torrent) {
  // Torrents can contain many files. Let's use the .mp4 file
  var file = torrent.files.find(function (file) {
    return file.name.endsWith('.mp4')
  })

  // Display the file by adding it to the DOM. Supports video, audio, image, etc. files
  file.appendTo('body')
})
		</script>
	</head>
	<body>
		
	</body>
</html>
